import { Button, Col, Image, List, Row, Space, Spin, Typography } from 'antd';
import React from 'react';
import { formattedAddress, removeIpfs } from '../utils';
import { useNavigate } from 'react-router-dom';
import ProgressiveImage from 'react-progressive-graceful-image';

const { Text } = Typography;

const placeholder =
  'data:image/png;base64,';

export default function WatchDetails({ watch, isSeller, write, isLoading }) {
  const navigate = useNavigate();

  return (
    <Row gutter={32} className="item">
      {!watch ? (
        <Spin />
      ) : (
        <>
          <Col xs={8}>
            <Row gutter={16}>
              <Col xs={24}>
                <ProgressiveImage
                  src={`https://gateway.pinata.cloud/ipfs/${removeIpfs(
                    watch.images[0]
                  )}`}
                  placeholder={placeholder}
                >
                  {(src, loading) => {
                    return (
                      <img
                        src={src}
                        alt=""
                        style={{ opacity: loading ? 0.5 : 1, width: '100%' }}
                      />
                    );
                  }}
                </ProgressiveImage>
              </Col>
              <Col xs={24}>
                <List
                  loading={!watch}
                  grid={{ column: 2, gutter: 8 }}
                  dataSource={watch.images.filter((photo, i) => i !== 0)}
                  renderItem={(url, i) => (
                    <List.Item>
                      <ProgressiveImage
                        src={`https://gateway.pinata.cloud/ipfs/${removeIpfs(
                          url
                        )}`}
                        placeholder={placeholder}
                      >
                        {(src, loading) => {
                          return (
                            <img
                              src={src}
                              alt=""
                              style={{
                                opacity: loading ? 0.5 : 1,
                                width: '100%'
                              }}
                            />
                          );
                        }}
                      </ProgressiveImage>
                    </List.Item>
                  )}
                />
                <Image
                  src={`https://gateway.pinata.cloud/ipfs/${removeIpfs(
                    watch.certificateUrl
                  )}`}
                  placeholder={placeholder}
                />
              </Col>
            </Row>
          </Col>
          <Col xs={16} className="item-right">
            <div>
              <Typography.Title level={2} style={{ marginTop: 0 }}>
                {watch.brand} {watch.model}
              </Typography.Title>
              <Typography.Text>
                Owned by{' '}
                <Typography.Text strong>
                  {isSeller ? 'me' : formattedAddress(watch.seller)}
                </Typography.Text>
              </Typography.Text>
            </div>
            <div style={{ marginTop: 20 }}>
              <Typography.Text>{watch.description}</Typography.Text>
            </div>
            <Space style={{ marginTop: 20, flexWrap: 'wrap' }}>
              {watch.attributes.map(
                (elt, i) =>
                  elt.trait_type && (
                    <div key={i} className="attributes-tag">
                      <Text
                        strong
                        style={{
                          fontSize: 12
                        }}
                      >
                        {elt.trait_type}
                      </Text>
                      <hr className="divider" />
                      <Text
                        style={{
                          fontSize: 12
                        }}
                      >
                        {elt.trait_type === 'Expert address'
                          ? formattedAddress(elt.value)
                          : elt.value}
                      </Text>
                    </div>
                  )
              )}
            </Space>
            <Space style={{ marginTop: 24 }}>
              <Button
                type="primary"
                size="large"
                disabled={isLoading}
                onClick={() => navigate(-1)}
              >
                Go back
              </Button>
              <Button
                type="primary"
                size="large"
                disabled={isSeller || !write}
                onClick={write}
                loading={isLoading}
              >
                Buy at {watch.totalPrice} ETH
              </Button>
            </Space>
          </Col>
        </>
      )}
    </Row>
  );
}
